INSERT INTO `tbl_paper` VALUES ('31', '', '曹国', '曹国', '', '计算机;;维修技术;;故障诊断', '吉林医药学院现代教育技术中心;', '148', '10', '2019-05-24 16:58', '电子技术与软件工程', 'CAPJ2019', '本文通过对计算机维修工作思路的相关内容进行阐述,进而分析我国在计算机维修中对具体故障的诊断及其处理方式,以更好地针对计算机运行中出现的故障提出有效的解决方案。', '计算机维修技术及故障诊断', '', '2019', 'https://kns.cnki.net/kns/download.aspx?filename=zkGb6FFTp9EZqh0L3BVSR50SDl0QwkTeuhUQZJHWpF2S1ZDcXl0UDd3dNFVVLFmSy02R30GVrtEe6pVbIlUa1U3TqhnU4kGc4U2N21UVKdzTZl1UykHdTFmVjR1Rk5GM5QTbV5EdyV3R3QkUnhDekNTWJB3b3F0L&tablename=CAPJLAST&dflag=pdfdown');
INSERT INTO `tbl_paper` VALUES ('32', '', '马碧芳;王伟;陈浴权;黄丽珍;王诗豪', '马碧芳', '', '虚拟现实技术;;福清光饼工艺;;非物质文化遗产;;传统技艺', '福建师范大学福清分校电子与信息工程学院;', '152-153+256', '10', '2019-05-24 16:58', '电子技术与软件工程', 'CAPJ2019', '虚拟现实(VR)技术是当今的一种新兴的科学技术,凭借其独特性,给人以逼真的“沉浸感”。论文通过虚拟现实技术还原传统的福清光饼制作工艺,设计了福清光饼非遗保护系统,并对用户的体验进行数据评估分析,分析结果表明,通过VR技术还原呈现福清光饼的诞生、发展及变迁,是可以为非物质文化遗产的保护提供一种有效的方法。', '基于VR技术的福清光饼非物质文化遗产保护系统研究', '', '2019', 'https://kns.cnki.net/kns/download.aspx?filename=WNDTl92MYlHdrJWdqd1SnlFNyoXTBpUNR90ZGRFOJlVUDRzLPhEcY9UYQlkVYdza2NzZqlnUnJEdvRlR58yMNVlewhzQ5lTZt9EMH12RGdkMz1mUWJjdyEERjhlUoVUNMJmT01kZFJjbyIGWxZ3bEJEdEZ3SFFzY&tablename=CAPJLAST&dflag=pdfdown');
